-- ============================================================================
-- 8. 휴가 잔액 (Leave Balance)
-- ============================================================================
INSERT INTO hr_attendance.leave_balance (id, tenant_id, employee_id, year, leave_type, total_days, used_days, pending_days, carried_over_days, created_at, updated_at)
SELECT
    gen_random_uuid(),
    e.tenant_id,
    e.id,
    2025,
    'ANNUAL',
    15.0,
    FLOOR(RANDOM() * 5)::DECIMAL,
    0,
    0,
    NOW(),
    NOW()
FROM hr_core.employee e;

-- ============================================================================
-- 9. 근태 기록 (최근 7일)
-- ============================================================================
INSERT INTO hr_attendance.attendance_record (id, tenant_id, employee_id, work_date, check_in_time, check_out_time, status, work_hours, created_at, updated_at)
SELECT
    gen_random_uuid(),
    e.tenant_id,
    e.id,
    CURRENT_DATE - i,
    TIME '09:00:00' + (RANDOM() * INTERVAL '30 minutes'),
    TIME '18:00:00' + (RANDOM() * INTERVAL '2 hours'),
    'NORMAL',
    480 + FLOOR(RANDOM() * 120)::INTEGER,  -- 분 단위 (8시간 + 0~2시간)
    NOW(),
    NOW()
FROM hr_core.employee e
CROSS JOIN generate_series(1, 7) AS i
WHERE EXTRACT(DOW FROM CURRENT_DATE - i) NOT IN (0, 6);  -- 주말 제외

