-- 완료 메시지
-- ============================================================================
SELECT
    '샘플 데이터 생성 완료!' as status,
    (SELECT COUNT(*) FROM tenant_common.tenant) as tenants,
    (SELECT COUNT(*) FROM hr_core.department) as departments,
    (SELECT COUNT(*) FROM hr_core.employee) as employees,
    (SELECT COUNT(*) FROM hr_attendance.attendance_record) as attendance_records;
